import React from 'react';
import { KEYBOARD_POSITIONS } from '../../constants';

function GuessInput({ handleGuess, isGameOver, keysStatus }) {
  const [guess, setGuess] = React.useState('');

  function handleSubmit(event) {
    event.preventDefault();

    handleGuess(guess);
    setGuess('');
  }

  function handleKey(event = null, letter = null) {
    if (event === null && letter === null) return;

    let newValue = '';
    if (event) {
      event.preventDefault();
      newValue = event.target.value.toUpperCase();
    } else {
      newValue = (guess + letter).toUpperCase();
    }

    // disallow known-missing letters
    const position = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.indexOf(newValue[newValue.length - 1]);
    if (newValue && (position === -1 || keysStatus[position].status === 'missing')) {
      beep();
      return;
    };

    if (newValue.match(/^[A-Z]{0,5}$/g)) {
      setGuess(newValue);
    } else {
      beep();
    }
  }

  function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
  }

  return (
    <>
      <form className="guess-input-wrapper" onSubmit={handleSubmit}>
        <label htmlFor="guess-input">Enter guess:</label>
        <input id="guess-input" type="text" autoFocus required
          disabled={isGameOver}
          pattern="^[A-Z]{5}$"
          value={guess}
          onChange={handleKey}
        />
      </form>
      <div className="guess-keyboard">
        {KEYBOARD_POSITIONS.map((row, index) => (
          <div
            className="guess-keyboard-row"
            key={index}
            style={{width: row.length * 37 - 4 + 'px'}}
          >
            {KEYBOARD_POSITIONS[index].map((position) => (
              <button type='button'
                key={position}
                className='guess-keyboard-key'
                data-key={keysStatus[position].letter}
                data-state={keysStatus[position].status}
                onClick={(event) => {
                  handleKey(null, keysStatus[position].letter);
                  document.getElementById('guess-input').focus();
                }}
              >{keysStatus[position].letter}</button>
            ))}
          </div>
        ))}
      </div>
    </>
  );
}

export default GuessInput;
